export var getFormatDatetime = function (datetime, format) {
    var origin = datetime.toISOString();
    var year = format.includes('YYYY')
        ? origin.substring(0, 4)
        : origin.substring(2, 4);
    var month = origin.substring(5, 7);
    var date = origin.substring(8, 10);
    var hour = origin.substring(11, 13);
    var minute = origin.substring(14, 16);
    var result = format
        .replace(/YYYY/g, year)
        .replace(/YY/g, year)
        .replace(/MM/g, month)
        .replace(/DD/g, date)
        .replace(/HH/g, hour)
        .replace(/mm/g, minute);
    return result;
};
export var getMonthArray = function (year, month) {
    var daysInMonth = new Date(year, month, 0, 9, 0, 0).getDate();
    var monthArray = [];
    for (var day = 1; day <= daysInMonth; day++) {
        var date = new Date(year, month - 1, day, 12, 0, 0);
        var formattedDate = getFormatDatetime(date, 'YYYY-MM-DD');
        var dayValue = date.getDate();
        var dayOfWeek = date.getDay();
        monthArray.push({
            ISO: formattedDate,
            date: dayValue,
            day: dayOfWeek,
        });
    }
    return monthArray;
};
